import React, { useState, useEffect } from 'react';
import SearchBox from '../components/SearchBox';
import './App.css';
import Errorboundry from '../components/Errorboundry';

function App() {
  const [searchfield, setSearchfield] = useState('');
  const [numberFact, setNumberFact] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNumberFact = async () => {
      try {
        const response = await fetch('http://numbersapi.com/random/trivia');
        const data = await response.text();
        setNumberFact(data);
        setLoading(false);
      } catch (error) {
        console.log(error);
      }
    };
    fetchNumberFact();
  }, []);

  const onSearchChange = event => {
    setSearchfield(event.target.value);
  };

  const loadNewFact = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://numbersapi.com/random/trivia');
      const data = await response.text();
      setNumberFact(data);
      setLoading(false);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className='tc'>
      <h1 className='f1'>Number Facts</h1>
      <SearchBox searchChange={onSearchChange} />
      {loading ? (
        <h2>Loading...</h2>
      ) : (
        <div>
          <p>{numberFact}</p>
          <button onClick={loadNewFact}>Load New Fact</button>
        </div>
      )}
    </div>
  );
}

export default App;
